#!/bin/bash
sudo tshark -e frame.interface_id -e frame.time -e frame.time_epoch -e frame.time_relative -e frame.number -e frame.len -e frame.protocols -e ip.version -e ip.hdr_len -e ip.len -e ip.ttl -e ip.proto -e ip.src -e ip.dst -e udp.srcport -e udp.dstport -e udp.length -e udp.stream -e udp.checksum.status -e tcp.srcport -e tcp.dstport -e tcp.stream -e tcp.ack -e tcp.hdr_len -e tcp.window_size -e tcp.analysis.out_of_order -e tcp.connection.fin -e tcp.connection.syn -e tcp.flags -e tcp.seq -e data.len -e dns.id -e dns.qry.name -e dns.resp.name -e dns.a -e dns.aaaa -e dns.response_to -e dns.resp.ttl -e dns.time -e http.host -e http.request.method -e http.request.version -e http.host -e http.request -e http.request_number -e ssl.handshake.cert_url.url -e ssl.handshake -e ssl.handshake.extensions_server_name -T json > tshark.json
